mapper.select:
select o.oid, ozipcode, oaddress1, oaddress2, oreceiver, ophone, otel, omemo, oemail, OUSEDMILEAGE, obeforeprice, oafterprice, ostatus, o.mid, o.pmcode, odate, o.cpid,
oi.psid, oicount, oitotalprice,
pmcompany, pmmethod,
ps.psize, ps.pcid, pi.pimg, pi.colorname, p.pname, pc.pcprice, b.bno, b.bname,
e.eno, e.ecoupontitle, ediscount
from ( select rownum rn, oid, ozipcode, oaddress1, oaddress2, oreceiver, ophone, otel, omemo, oemail, OUSEDMILEAGE, obeforeprice, oafterprice, ostatus, mid, pmcode, odate, cpid from( select * from orders where mid = #{mId}
and odate >= add_months(sysdate, #{cri.month} * -1) order by odate
) where rownum <= #{cri.currentPage} * #{cri.pageSize}
)o left join order_item oi on (o.oid = oi.oid) left join payment_method pm on (pm.pmcode = o.pmcode) left join product_stock ps on (ps.psid = oi.psid) left join product_color pc on (pc.pcid = ps.pcid) left join product_color_info pi on (pi.pcid = pc.pcid) left join product_common p on (pc.pid = p.pid) left join brand b on (p.bno = b.bno) left join coupon_detail cp on (cp.cpid = o.cpid) left join event e on (e.eno = cp.eno) where rn > (#{cri.currentPage} - 1) * #{cri.pageSize}

mapper.update:
UPDATE ORDERS 
SET OSTATUS = '주문 취소'
WHERE OID = #{oId}

mapper.select:
SELECT OID 
FROM ORDERS o, PAYMENT_METHOD pm 
WHERE o.PMCODE = pm.PMCODE AND pm.PMCODE = #{merchant_uid}
