mapper.select:
select count(*)
from likes 
where mid = #{mid} and pid = #{pid}

            select rn,
				l.pid, p.pname, p.pnote, b.bno, p.pstatus,
				pc.pcid, pcimg1, pcimg2, pcimg3, pcchipimg, pccolorcode, pcprice,
				b.bname
			from(	
				select rownum rn, pid
				from(
					select pid
					from likes 
					where mid =#{mId}
					order by ltime desc
				) a
				
				where rownum <= #{cri.currentPage} * #{cri.pageSize}
			) l
			inner join product_common p on (l.pid = p.pid)
			left join brand b on (p.bno = b.bno)
			inner join product_color pc on (pc.pid = p.pid)
            where rn > (#{cri.currentPage} - 1) * #{cri.pageSize}

SELECT sub.pid, sub.pcid, sub.pname, sub.bname, sub.pstatus, sub.bno, sub.pcprice, sub.pccolorcode, sub.pcimg1, sub.pcchipimg,sub.psstock, sub.preleasedate, sub.rnum FROM ( SELECT rsub.pid, rsub.pname, rsub.bname, rsub.pstatus, rsub.bno, rsub.rnum, pcolor.pcid, pcolor.pcprice, pcolor.pccolorcode, pcolor.pcimg1, pcolor.pcchipimg, pcolor.preleasedate, pstock.psstock FROM (SELECT pcom.pid, pcom.pname, pcom.bname, pcom.pstatus, pcom.bno , ROW_NUMBER() over (order by pcom.pid) rnum from product_common pcom INNER JOIN product_category pcat ON pcom.pid = pcat.pid INNER JOIN category c
ON c.cateno = pcat.cateno and c.depth1name = #{gd}
AND c.depth2name = #{sC}
AND c.depth3name = #{tC}
) rsub INNER JOIN product_color pcolor ON pcolor.pid = rsub.pid INNER JOIN product_stock pstock ON pstock.pcid = pcolor.pcid group by rsub.pid, pcolor.pcid, rsub.pname, rsub.bname, rsub.pstatus, rsub.bno, pcolor.pcprice, pcolor.pccolorcode, pcolor.pcimg1, pcolor.pcchipimg, pstock.psstock, pcolor.preleasedate, rsub.rnum) sub WHERE rnum BETWEEN #{start} * 14 AND (#{last} * 14) ORDER BY rnum

SELECT COUNT(pcom.pid) as cnt
from product_common pcom
INNER JOIN product_category pcat
ON pcom.pid = pcat.pid
INNER JOIN category c

	ON c.cateno = pcat.cateno and c.depth1name = #{gd}

	AND c.depth2name = #{sC}


	AND c.depth3name = #{tC}

select pcolor.pcchipimg, pcolor.pccolorcode, pcolor.pcid, pcolor.pid
from product_color pcolor
where pcolor.pid = #{pid}

SELECT b.BNAME, pc.PNAME
FROM PRODUCT_COMMON pc, BRAND b  
WHERE pc.BNO = b.BNO AND PID = #{pId}
