mapper.select:
            select rn,
				l.pid, p.pname, p.pnote, b.bno, p.pstatus,
				pc.pcid, pcimg1, pcimg2, pcimg3, pcchipimg, pccolorcode, pcprice,
				b.bname
			from(	
				select rownum rn, pid
				from(
					select pid
					from likes 
					where mid =#{mId}
					order by ltime desc
				) a
				
				where rownum <= #{cri.currentPage} * #{cri.pageSize}
			) l
			inner join product_common p on (l.pid = p.pid)
			left join brand b on (p.bno = b.bno)
			inner join product_color pc on (pc.pid = p.pid)
            where rn > (#{cri.currentPage} - 1) * #{cri.pageSize}

SELECT pcom.pid, pcolor.pcid, pcom.pname, pcom.bname, pcom.pstatus, pcom.bno, pcolor.pcprice, pcolor.pccolorcode, pcolor.pcimg1, pcolor.pcchipimg, pstock.psstock from product_common pcom INNER JOIN product_category pcat ON pcom.pid = pcat.pid INNER JOIN category c
ON c.depth1name = #{gd}
AND ON c.depth2name = #{sC}
AND ON c.depth3name = #{tC}
INNER JOIN product_color pcolor ON pcolor.pid = pcom.pid INNER JOIN product_stock pstock ON pstock.pcid = pcolor.pcid where psid like 'MM2C7KTO041H9E%' group by pcom.pid, pcolor.pcid, pcom.pname, pcom.bname, pcom.pstatus, pcom.bno, pcolor.pcprice, pcolor.pccolorcode, pcolor.pcimg1, pcolor.pcchipimg, pstock.psstock

SELECT b.BNAME, pc.PNAME
FROM PRODUCT_COMMON pc, BRAND b  
WHERE pc.BNO = b.BNO AND PID = #{pId}
